  productsAfterSearch:Iproducts[]=[];
 
  searchwithcategory(event: Event) {
    const target = event.target as HTMLSelectElement;
    if (target) {
      const selectedValue = target.value;
      let cidNum = parseInt(selectedValue);
      this.prodWithApiService.getproductbycategoryid(cidNum).subscribe({
        next: (data) => {
          this.productsAfterSearch = data;
          console.log(data);
        },
        error: (err) => {
          console.log(err);
        }
      });
    }
  }
  
  DeleteIt(prodid : number ) : void {
    const warn = window.confirm("are you sure ?!") ; 
    if (warn ){
      this.prodWithApiService.deleteProduct(prodid).subscribe(()=>{
       this.productwithfilter=this.productwithfilter.filter((productsAfterSearch)=>productsAfterSearch.id!==prodid)
      })
    }
  }


